import React from 'react';
import Header from './Header';


class CharacterFilter extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      placeholder: "&#xF002; Search a character...",
    }



  }



  render() {
    const placeholderHandler = () => {
      this.setState({
        placeholder: ''
      });
    }
    const onChangeHandler = event => {
      //event.preventDefault();
      this.props.onChangeHandler(event.target.value);
    }
  return (
    <React.Fragment>
      <Header/>
      <form value={this.props.inputValue} className="filter-wrapper">
      <input onFocus={placeholderHandler} onBlur={placeholderHandler} className="search-characters"  placeholder={(this.state.placeholder === '')? '': "Search a character..."} onChange={onChangeHandler} type='text' value={this.props.inputValue}></input>
      </form>
    </React.Fragment>
  )
}
}
export default CharacterFilter;
